using System;namespace Thomsen.GuessingGame{	public class Guesser	{		public int currentMin;		public int currentMax;	    public int currentGuess;	    public int count;		public int Guess (int min, int max)		{			currentMin = min;			currentMax = max;		    Random random = new Random(DateTime.Now.Millisecond+DateTime.Now.Millisecond);		    currentGuess = random.Next(currentMin, currentMax+1);		    count++;		    return currentGuess;		}	}}